import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  numericalValues: number[] = [];

  constructor() { }

  ngOnInit(): void {
    // Simulated data update every second
    setInterval(() => {
      this.numericalValues = this.generateRandomValues();
    }, 1000);
  }

  private generateRandomValues(): number[] {
    // Generate 8 random numerical values for demonstration
    return Array.from({ length: 8 }, () => Math.floor(Math.random() * 100));
  }
}
<div fxLayout="row" fxLayout.xs="column" fxLayoutAlign="start start" fxLayoutGap="16px">
  <!-- Left Grid Element -->
  <div fxFlex="50%" fxFlex.xs="100%">
    <mat-card>
      <mat-card-content>
        <h3>{{title}}</h3>
        <div class="scrollable-data">
          <mat-list>
            <mat-list-item *ngFor="let value of numericalValues">
              <h4 mat-line>Numerical Value:</h4>
              <p mat-line>{{ value }}</p>
            </mat-list-item>
          </mat-list>
        </div>
      </mat-card-content>
    </mat-card>
  </div>

  <!-- Right Grid Element -->
  <div fxFlex="50%" fxFlex.xs="100%">
    <mat-card>
      <mat-card-content>
        <h3>Title</h3>
        <!-- Add content for the right grid here -->
      </mat-card-content>
    </mat-card>
  </div>
</div>
